import { UserRegister } from "../../../core/index_login";
export default {
  data: function data() {
    return {
      param: {
        uName: '',
        uNumber: '',
        phone: '',
        emaile: '',
        status: 2
      },
      rules: {
        uName: [{
          required: true,
          message: '请输入用户名',
          trigger: 'blur'
        }],
        uNumber: [{
          required: true,
          message: '请输入账号',
          trigger: 'blur'
        }],
        phone: [{
          required: true,
          message: '请输入手机号',
          trigger: 'blur'
        }, {
          pattern: /^((0\d{2,3}-\d{7,8})|(1[3584]\d{9}))$/,
          message: "请输入合法手机号/电话号",
          trigger: "blur"
        }],
        emaile: [{
          required: true,
          message: '请输入邮箱',
          trigger: 'blur'
        }, {
          pattern: /^([0-9A-Za-z\-_\.]+)@([0-9a-z]+\.[a-z]{2,3}(\.[a-z]{2})?)$/g,
          message: "请输入正确的邮箱",
          trigger: "blur"
        }]
      }
    };
  },
  methods: {
    submitForm: function submitForm() {
      var _this = this;

      this.$refs.login.validate(function (valid) {
        if (valid) {
          var aData = new Date();
          _this.param.createDate = aData.getFullYear() + "-" + (aData.getMonth() + 1) + "-" + aData.getDate();
          console.log(_this.param);
          if (_this.param.phone) UserRegister(_this.param).then(function (res) {
            console.log(res);
            localStorage.setItem('ms_registeruid', JSON.stringify(res.data.user));

            _this.$message.success('注册成功');

            _this.$router.push('/registerfinal');

            localStorage.setItem('ms_token', res.data.token);
          });
        } else {
          _this.$message.error('请输入完整信息');

          console.log('error submit!!');
          return false;
        }
      });
    },
    registernew: function registernew() {
      this.$router.push('/register');
    },
    comeback: function comeback() {
      this.$router.push('/login');
    }
  }
};
