import { fetchUserData, ModifyUserData, AddUserData, AddUserPData, SearchUserData, DelUserData, SearchUserPData } from "../../../core/index_user";
import { ModifyStuData } from "../../../core/index_student";
import { ModifyLoginData } from "../../../core/index_login";
export default {
  name: 'userfrom',
  data: function data() {
    return {
      query: {},
      query2: {
        uName: ""
      },
      temp: {},
      tempsa: {
        peId: 0
      },
      formn: {},
      formp: {
        status: "教师"
      },
      temps: [],
      tableData: [],
      tableData2: [],
      multipleSelection: [],
      delList: [],
      addVisible: false,
      editVisible: false,
      detVisible: false,
      editMisible: false,
      pageTotal: 0,
      form: {},
      formd: {},
      formm: {},
      foemp: {
        uId: 1,
        sId: 1,
        classes: 1,
        isTeacher: 1
      },
      formadd: {},
      idx: -1,
      id: -1
    };
  },
  created: function created() {
    this.getData();
  },
  methods: {
    // 获取 easy-mock 的模拟数据
    getData: function getData() {
      var _this = this;

      fetchUserData(this.query).then(function (res) {
        _this.tableData = res;
        _this.pageTotal = _this.tableData.length;
      });
      this.query2.uName = "";
    },
    // 触发搜索按钮
    handleSearch: function handleSearch() {
      var testN = this.query2.uName;
      this.tableData = this.tableData.filter(function (item) {
        return item.uName == testN;
      });
    },
    handleM: function handleM(index, row) {
      this.idx = index;
      this.formm.uId = row.uId;
      console.log(row);
      this.editMisible = true;
    },
    // 删除操作
    handleDelete: function handleDelete(index, row) {
      var _this2 = this;

      // 二次确认删除
      this.$confirm('确定要删除吗？', '提示', {
        type: 'warning'
      }).then(function () {
        if (_this2.tableData.length > 1) {
          _this2.temp = row;
          DelUserData(_this2.temp).then(function (res) {
            if (res == 1) {
              _this2.$message.success('删除成功');

              _this2.tableData.splice(index, 1);
            } else {
              _this2.$message.error('删除失败，请通知管理员！');
            }
          });
        } else {
          _this2.$message.error('数据库内至少需要保留一条信息！');
        }
      }).catch(function () {});
    },
    // 多选操作
    handleSelectionChange: function handleSelectionChange(val) {
      this.multipleSelection = val;
    },
    delAllSelection: function delAllSelection() {
      var length = this.multipleSelection.length;
      var str = '';
      this.delList = this.delList.concat(this.multipleSelection);

      for (var i = 0; i < length; i++) {
        str += this.multipleSelection[i].uName + ' ';
      }

      this.$message.error("\u5220\u9664\u4E86".concat(str));
      this.multipleSelection = [];
    },
    // 新增用户
    handleAdd: function handleAdd() {
      this.addVisible = true;
    },
    //详情查看
    handleDet: function handleDet(index, row) {
      var _this3 = this;

      this.idx = index;
      this.tempsa.peId = row.peId;
      SearchUserPData(this.tempsa).then(function (res) {
        _this3.formd = res;
      });
      this.detVisible = true;
    },
    //新增操作
    saveAdd: function saveAdd(tableData, event) {
      this.addVisible = false;
      this.$message.success("\u65B0\u589E\u6210\u529F");

      if (this.formadd.status == "管理员") {
        this.formadd.status = 1;
      } else if (this.formadd.status == "教师") {
        this.formadd.status = 2;
      } else if (this.formadd.status == "学生") {
        this.formadd.status = 3;
      } else {
        this.formadd.status = 0;
      }

      var aData = new Date();
      this.formadd.createDate = aData.getFullYear() + "-" + (aData.getMonth() + 1) + "-" + aData.getDate(); //this.foemp.peName = this.formadd.peName;
      //delete this.formadd.peName;

      AddUserData(this.formadd); //AddUserPData(this.foemp);

      this.getData();
    },
    // 编辑操作
    handleEdit: function handleEdit(index, row) {
      var _this4 = this;

      this.idx = index;
      this.form = row;
      this.tempsa.peId = row.peId;
      SearchUserPData(this.tempsa).then(function (res) {
        _this4.formd = res;
      });
      if (this.form.status == 1) this.formp.status = "管理员";else if (this.form.status == 2) this.formp.status = "教师";else if (this.form.status == 3) this.formp.status = "学生";else this.formp.status = "未知";
      this.editVisible = true;
    },
    // 保存编辑
    saveEdit: function saveEdit() {
      this.editVisible = false;
      this.$message.success("\u4FEE\u6539\u7B2C ".concat(this.idx + 1, " \u884C\u6210\u529F"));
      if (this.formp.status == "管理员") this.form.status = 1;else if (this.formp.status == "教师") this.form.status = 2;else if (this.formp.status == "学生") this.form.status = 3;else this.form.status = 0;
      this.temp = this.form;
      ModifyUserData(this.temp);
      if (this.form.status == 2) this.formd.isTeacher = 1;else this.formd.isTeacher = 0;
      ModifyStuData(this.formd);
    },
    saveEditM: function saveEditM() {
      this.editMisible = false;
      this.$message.success("\u4FEE\u6539\u5BC6\u7801\u6210\u529F");
      ModifyLoginData(this.formm).then(function (res) {
        console.log(res);
      });
    },
    //保存详情
    saveEditU: function saveEditU() {
      this.editVisible = false;
      this.$message.success("\u4FEE\u6539\u6210\u529F");
      if (this.formd.sId == null) this.formd.sId = 1;
      this.temp = this.formd;
      ModifyStuData(this.temp);
      this.detVisible = false;
    },
    // 分页导航
    handlePageChange: function handlePageChange(val) {
      this.$set(this.query, 'pageIndex', val);
      this.getData();
    }
  }
};
