import request from '../shared/components/request';
import qs from 'qs';

export const fetchUserData = query => {
    return request({
        url: '/api/user/findAll.do',
        method: 'get',
        params: query,
        headers:{
            'token':localStorage['ms_token']
        }
    });
};

export const ModifyUserData = (query) => {
    return request({
        url: '/api/user/updateByPrimaryKey.do',
        method: 'post',
        data: query,
        emulateJSON: true,
        headers:{
            'token':localStorage['ms_token']
        }
    });
};

export const AddUserData = (query) => {
    return request({
        url: '/api/user/insert.do',
        method: 'post',
        data: query,
        emulateJSON: true,
        headers:{
            'token':localStorage['ms_token']
        }
    });
};

export const DelUserData = (query) => {
    return request({
        url: '/api/user/deleteByPrimaryKey.do',
        method: 'post',
        data: query,
        emulateJSON: true,
        headers:{
            'token':localStorage['ms_token']
        }
    });
};

export const SearchUserData = (query) => {
    return request({
        url: '/api/user/selectByPrimaryKey.do',
        method: 'post',
        data: query,
        emulateJSON: true,
        headers:{
            'token':localStorage['ms_token']
        }
    });
};

export const fetchRoleData = query => {
    return request({
        url: '/api/role/findAll.do',
        method: 'get',
        params: query,
        headers:{
            'token':localStorage['ms_token']
        }
    });
};


export const sendToken = query => {
    return request({
        url: '/api/user/findAll.do',
        method: 'get',
        params: query
    });
};
