import bus from "../default/bus";
import { fetchRoleData } from "../../core/index_role";
export default {
  name: 'sidebar',
  data: function data() {
    return {
      tableData: [],
      name: localStorage.getItem('ms_username'),
      root_test: localStorage.getItem('ms_status'),
      collapse: false,
      n: 0,
      items: [{
        icon: 'el-icon-lx-home',
        index: 'dashboard',
        title: '系统首页',
        unknown: 'all'
      }, {
        icon: 'el-icon-lx-calendar',
        index: 'user',
        title: '用户信息',
        unknown: 'all'
      }, {
        icon: 'el-icon-lx-copy',
        index: 'class',
        title: '班课信息',
        unknown: 'all'
      }, {
        icon: 'el-icon-lx-copy',
        index: 'level',
        title: '我的班课',
        unknown: 'all'
      }, {
        icon: 'el-icon-s-order',
        index: 'tabs',
        title: '个人消息',
        unknown: 'all'
      }, {
        icon: 'el-icon-s-order',
        index: 'role',
        title: '角色信息',
        unknown: '1'
      }, {
        icon: 'el-icon-document',
        index: 'college',
        title: '院校信息',
        unknown: 'no'
      }, {
        icon: 'el-icon-lx-warn',
        index: '7',
        title: '错误处理',
        unknown: 'no',
        subs: [{
          index: 'permission',
          title: '权限测试'
        }, {
          index: '404',
          title: '404页面'
        }]
      }, {
        icon: 'el-icon-lx-warn',
        index: '8',
        title: '高级权限',
        unknown: '1',
        subs: [{
          index: 'set',
          title: '参数设置'
        }, {
          index: 'datadictionary',
          title: '数据字典'
        }]
      }, {
        icon: 'el-icon-lx-global',
        index: 'i18n',
        title: '使用说明',
        unknown: 'no'
      }]
    };
  },
  computed: {
    onRoutes: function onRoutes() {
      return this.$route.path.replace('/', '');
    }
  },
  created: function created() {
    var _this = this;

    fetchRoleData(this.query).then(function (res) {
      _this.tableData = res;
      if (_this.root_test == '1') _this.n = 2;else if (_this.root_test == '2') _this.n = 1;else {
        _this.$router.push('/login');
      }
      _this.pageTotal = _this.tableData.length;

      for (var x = 1, y = 0; y < 4; y++) {
        x = 0 + _this.tableData[_this.n].permissions[y].pId;

        if (_this.tableData[_this.n].permissions[y].status == 0) {
          _this.items[x].unknown = 'no';
        } else {
          _this.items[x].unknown = 'all';
        }
      }
    }); // 通过 Event Bus 进行组件间通信，来折叠侧边栏

    bus.$on('collapse', function (msg) {
      _this.collapse = msg;
      bus.$emit('collapse-content', msg);
    });
  }
};
