import bus from '../default/bus';
export default {
    data() {
        return {
            collapse: false,
            items: [
                {
                    icon: 'el-icon-lx-home',
                    index: 'dashboard',
                    title: '系统首页',
                    unknown:true
                },
                {
                    icon: 'el-icon-lx-calendar',
                    index: 'user',
                    title: '用户信息',
                    unknown:true
                },
                {
                    icon: 'el-icon-lx-copy',
                    index: 'tabs',
                    title: '个人消息',
                    unknown:true
                },
                {
                    icon: 'el-icon-lx-warn',
                    index: '7',
                    title: '错误处理',
                    unknown:false,
                    subs: [
                        {
                            index: 'permission',
                            title: '权限测试'
                        },
                        {
                            index: '404',
                            title: '404页面'
                        }
                    ]
                },
                {
                    icon: 'el-icon-lx-warn',
                    index: 'level',
                    title: '等级设置',
                    unknown:true
                },
                {
                    icon: 'el-icon-lx-global',
                    index: 'i18n',
                    title: '使用说明',
                    unknown:true
                }
            ]
        };
    },
    computed: {
        onRoutes() {
            return this.$route.path.replace('/', '');
        }
    },
    created() {
        // 通过 Event Bus 进行组件间通信，来折叠侧边栏
        bus.$on('collapse', msg => {
            this.collapse = msg;
            bus.$emit('collapse-content', msg);
        });
    }
};
